DROP TABLE IF EXISTS `BON_LEAVE_BALANCES`;
CREATE TABLE `BON_LEAVE_BALANCES` (
  `EMPLOYEE_NUMBER` VARCHAR(30) NULL,
  `LEAVE` VARCHAR(80) NULL,
  `CYCLE_BALANCE_TO_DATE` DECIMAL(10,4) NULL,
 `END_OF_CYCLE_DATE` DECIMAL(10,4) NULL,
 `FULL_CYCLE_DAYS` DECIMAL(10,4) NULL,
 `CYCLE_DAYS_TAKEN_TO_DATE` DECIMAL(10,4) NULL
);
DROP TABLE IF EXISTS `BON_LEAVE_DETAIL`;
CREATE TABLE `BON_LEAVE_DETAIL` (
  `EMPLOYEE_NUMBER` VARCHAR(30) NULL,
  `DATE_START` DATE NULL,
  `DATE_END` DATE NULL,
 `LEAVE_TYPE` VARCHAR(80) NULL,
 `ABSENCE_DAYS` DECIMAL(10,4) NULL
);



